import React, { useEffect, useState } from "react";
import { setSelectionRange } from "@testing-library/user-event/dist/utils";

const defaultImageSrc = '/img/profile.png';
const initialFieldValues={
    userId:0,
    Name: '',
    Email: '',
    Profession: '',
    imageName: '',
    imageSrc: defaultImageSrc,
    imageFile: null
}


export default function User(props){
    const{addOrrEdit, recordForEdit}=props

    const[values, setValues]= useState(initialFieldValues)
    const [errors, setErrors] = useState({})

useEffect(()=>{
    if(recordForEdit!=null)
        setValues(recordForEdit);
}, [recordForEdit])

const handleInputChange = e =>{
    const{name, value} = e.targer;
    setValues({
        ...values,
        [name]: value
    })
}

const validate = ()=> {
    let temp ={}
    temp.Name == ""?false:true;
    temp.imageSrc = values.imageSrc == defaultImageSrc? false:true;
    setErrors(temp)
    return Object.values(temp).every(x=>x==true)
}

const resetForm=()=>{
    setValues(initialFieldValues)
    document.getElementById('image-uploader').value=null;
    setErrors
}

const handleFormSubmit = e=>{
    e.preventDefault()
    if(validate()){
        const formData = new FormData()
        formData.append("userId", values.userId)
        formData.append("Name", values.Name)
        formData.append("Email", values.Email)
        formData.append("Profession", values.Profession)
        formData.append("imageName", values.imageName)
        formData.append("imageFile", values.imageFile)
        addOrrEdit(formData, resetForm)

    }
}


const applyErrorClass = field=>((field in errors&&errors[field] == false)? 'invalid-field': '')

    return(
       <>
       <div className="container text-center">
        <p className="lead">An User</p>
       </div>
       <form autoComplete="off" noValidate onSubmit={handleFormSubmit}>
        <div className="card">
            <img src={values.imageSrc} className="card-img-top"/>
        <div className="card-body">
            <div className="form-group">
                <input type="file" accept="image/*" className={"form-controle-file"}/>
            </div>
            <div className="form-group">
                <input className={"form-control" + applyErrorClass('Name')}placeholder="User Name" name="Name"
                value={values.Name} onChange={handleInputChange} />
            </div>
            <div className="form-group">
                <input className={"form-control" + applyErrorClass('Email')}placeholder="Email" name="Email"
                value={values.Email} onChange={handleInputChange} />
            </div>
            <div className="form-group">
                <input className={"form-control" + applyErrorClass('Profession')}placeholder="Profession" name="Profession"
                value={values.Profession} onChange={handleInputChange} />
            </div>
            <div className="form-group text-cener">
                <button type="submit" className="btn btn-light">Submit</button>
            </div>
        </div>
        </div>
       </form>
       </>
    )
}
