import React,{useState, useEffect} from "react";
import User from "./User";
import axios from "axios";


export default function UserList(){
const [userList, setUserList] = useState([])
const [recordForEdit, setRecordForEdit] = useState(null)

useEffect(()=>{
    refreshUserList();
}, [])

const userAPI=(url = 'http://localhost:5262/api/User/')=>{
    return{
        fetchAll: ()=>axios.get(url),
        create: newRecord=>axios.post(url, newRecord),
        update: (id, updatedRecord)=> axios.put(url+id, updatedRecord),
        delete: id=> axios.delete(url+id)
    }
}

function refreshUserList(){
    userAPI().fetchAll()
    .then(res=>setUserList(res.data))
    .catch(err=>console.log(err))
}

const addOrrEdit=(formData, onSuccess)=>{
    if(FormData.get('Id')=="0"){
        userAPI().create(formData)
        .then(res=>{
            onSuccess();
            refreshUserList();
        })
        .catch(err=>console.log(err))
    }
    else{
        userAPI().update(formData.get('Id'), formData)
        .then(res=>{
            onSuccess();
            refreshUserList();
        })
        .catch(err=>console.log(err))
    }
}

const showRecordDetails=data=>{
    setRecordForEdit(data)
}

onDelete = (e, id)=>{
    e.stopPropagation();
    if(window.confirm("Are you sure to delete this record?"))
        userAPI().delete(id)
        .then(res=>refreshUserList())
        .catch(err=>console.log(err))
}

const imageCard = data=>(
    <div className="card" onClick={()=>{showRecordDetails(data)}}>
        <img src={data.imageSrc} className="card0img-top rounded-circle"/>
        <div className="card-body"></div>
    </div>
)

    return(
       <div className="row">
        <div className="col-md-12">
            <div className="jumotron jumbotron-fluid py-4">
                <div className="contrainer text-center">
                    <h1 className="display-4">User Register</h1>
                </div>
            </div>
        </div> 
        <div className="col-md-4">
         <User/>
        </div>
        <div className="col-md-8">
        </div>
       </div>
    )
}
